import React from "react";
import { Breadcrumb, Button, ButtonGroup, Row, Col, InputGroup, Form, Dropdown, Table, Card, DropdownButton } from "@themesberg/react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faPlus, faCog, faCheck, faSearch, faSlidersH } from '@fortawesome/free-solid-svg-icons';

import Popup from 'reactjs-popup';    
import teams from '../data/teams';
// import RankCard from '../components/';

class RankCard extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            clicked: [false]
        };
        for (var i = 1; i <= 8; i++) {
            this.state.clicked.push(false);
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e, ID) {
        var clickArr = [false];
        for (var i = 1; i <= 8; i++) {
            clickArr.push(i == ID ? true : false);
        }
        this.setState({
            clicked: clickArr
        });

        console.log(this.state.clicked);
    }

    render() {
        return (
            <tbody>
                { teams.map((team) => (
                    <>
                        <tr onClick={ (e) => this.handleClick(e, team.ID) }>
                            <th className="border-bottom" style={{ "fontSize": 24, "fontWeight": "bold", "align": "center" }}>#{ team.rank }</th>
                            <th className="border-bottom" style={{ "fontSize": 20 }}>{ team.ID }</th>
                            <th className="border-bottom" style={{ "fontSize": 20, "fontStyle": "italic" }}>{ team.score }</th>
                        </tr>
                        {
                            this.state.clicked[team.ID] ? (
                                <tr>
                                    <th colSpan={ 3 }>
                                        <Table borderless className="user-table align-items-center">
                                            <thead>
                                                <tr>
                                                    <th className="border-bottom">RFID</th>
                                                    <th className="border-bottom">Score</th>
                                                    <th className="border-bottom">Timestamp</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                <tr>
                                                    <th className="border-bottom" style={{ "fontSize": 20, "fontWeight": "bold" }}>12345678</th>
                                                    <th className="border-bottom" style={{ "fontSize": 16, "fontStyle": "italic" }}>12</th>
                                                    <th className="border-bottom" style={{ "fontSize": 16 }}>2022-07-01 01:29</th>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </th>
                                </tr>
                            ) : (
                                <></>
                            )
                        }
                    </>
                )) }
            </tbody>
        );
    }
}

export default () => {

    return (
        <div className="d-lg-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
            <div className="mb-4 mb-lg-0" style={{ width: '100%' }}>
                <Table borderless className="user-table align-items-center">
                    <thead>
                        <tr>
                            <th className="border-bottom" style={{ "fontSize": 28}}>Rank</th>
                            <th className="border-bottom" style={{ "fontSize": 28}}>Team ID</th>
                            <th className="border-bottom" style={{ "fontSize": 28}}>Score</th>
                        </tr>
                    </thead>
                    <RankCard></RankCard>
                </Table>
            </div>
        </div>
    );
};
