import React from "react";
import { Breadcrumb, Button, ButtonGroup, Row, Col, InputGroup, Form, Dropdown, Table, Card, DropdownButton } from "@themesberg/react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faPlus, faCog, faCheck, faSearch, faSlidersH } from '@fortawesome/free-solid-svg-icons';

import teams from '../data/teams';

export default () => {

    const [state, setState] = React.useState(false);
    const handleShowHistory = (e) => {
        setState(!state);
        console.log(e.target);
    };

    return (
        <div className="d-lg-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
            <div className="mb-4 mb-lg-0" style={{ width: '100%' }}>
                <h2>Ranking List</h2>

                <div className="table-settings mb-4">
                    <Row className="justify-content-between align-items-center">
                        <Col xs={9} lg={4} className="d-flex">
                            <InputGroup className="me-2 me-lg-3">
                                <InputGroup.Text>
                                    <FontAwesomeIcon icon={faSearch} />
                                </InputGroup.Text>
                                <Form.Control type="text" placeholder="Search" />
                            </InputGroup>
                        </Col>
                    </Row>
                </div>

                <Card border="light" className="table-wrapper table-responsive shadow-sm">
                    <Card.Body>
                        <Table hover className="user-table align-items-center">
                            <thead>
                                <tr>
                                    <th className="border-bottom">Rank</th>
                                    <th className="border-bottom">Team ID</th>
                                    <th className="border-bottom">Score</th>
                                    <th className="border-bottom">History</th>
                                </tr>
                            </thead>
                            <tbody>
                                { teams.map((team) => (
                                    <tr>
                                        <td style={{ fontSize: 20, fontWeight: "bold" }}>#{ team.rank }</td>
                                        <td style={{ fontSize: 18 }}>{ team.ID }</td>
                                        <td style={{ fontSize: 18, fontStyle: "italic" }}>{ team.score }</td>
                                        <td>
                                            <DropdownButton
                                                as={ ButtonGroup }
                                                drop="left"
                                                title="show"
                                                id="dropdown-basic-button">
                                                { team.history.map((record) => (
                                                    <Dropdown.Item as="button">
                                                        { record.RFID }
                                                    </Dropdown.Item>
                                                )) }
                                            </DropdownButton>
                                        </td>
                                    </tr>
                                )) }
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
            </div>
        </div>
    );
};
