import React from "react";
import { Breadcrumb, Button, ButtonGroup, Row, Col, InputGroup, Form, Dropdown, Table, Card, DropdownButton } from "@themesberg/react-bootstrap";
import { SlideDown } from 'react-slidedown';
import 'react-slidedown/lib/slidedown.css'

import teamsUpdater from '../../data/teams';

class Ranking extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            clicked: [false], 
            teams: teamsUpdater()
        };
        for (var i = 1; i <= 8; i++) {
            this.state.clicked.push(false);
        }
        this.handleClick = this.handleClick.bind(this);
        this.handleBlur = this.handleBlur.bind(this);
    }

    componentDidMount() {
        let state = {...this.state};
        state.teams = teamsUpdater();
        this.setState();
    }

    handleClick(e, ID) {
        let clickedArray = new Array(8 + 1).fill(false);
        clickedArray[ID] = !this.state.clicked[ID];
        this.setState({ clicked: clickedArray });
    }

    handleBlur(e) {
        let clickedArray = new Array(8 + 1).fill(false);
        this.setState({ clicked: clickedArray });
    }

    render() {
        return (
            <Table borderless className="align-items-center">
                <thead>
                    <tr>
                        <th className="border-bottom" style={{ "fontSize": "1.7rem" }}>Rank</th>
                        <th className="border-bottom" style={{ "fontSize": "1.7rem" }}>Team ID</th>
                        <th className="border-bottom" style={{ "fontSize": "1.7rem" }}>Score</th>
                    </tr>
                </thead>
                <tbody tabIndex={ 0 } onBlur={ this.handleBlur }>
                    <tr>
                        <th colSpan={ 3 }></th>
                    </tr>
                    { teams().map((team) => (
                        <>
                            <tr onClick={ (e) => this.handleClick(e, team.ID) } className="rank">
                                <th className="border-bottom" style={{ "fontSize": "1.5rem", "fontWeight": "bold"}}>#{ team.rank }</th>
                                <th className="border-bottom" style={{ "fontSize": "1.3rem" }}>{ team.ID }</th>
                                <th className="border-bottom" style={{ "fontSize": "1.3rem", "fontStyle": "italic" }}>{ team.score }</th>
                            </tr>
                            <tr>
                                <th colSpan={ 3 }>
                                    <SlideDown closed={ !this.state.clicked[team.ID] }>
                                    <Card border="light" className="table-wrapper table-responsive shadow-sm history">
                                        <Card.Body>
                                            <Table borderless className="user-table align-items-center">
                                                <thead>
                                                    <tr>
                                                        <th className="border-bottom" style={{ "fontSize": "1.1rem" }}>RFID</th>
                                                        <th className="border-bottom" style={{ "fontSize": "1.1rem" }}>Score</th>
                                                        <th className="border-bottom" style={{ "fontSize": "1.1rem" }}>Timestamp</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    <tr>
                                                        <th className="border-bottom" style={{ "fontSize": "1.0rem", "fontWeight": "bold" }}>12345678</th>
                                                        <th className="border-bottom" style={{ "fontSize": "0.9rem", "fontStyle": "italic" }}>12</th>
                                                        <th className="border-bottom" style={{ "fontSize": "0.9rem" }}>2022-07-01 01:29</th>
                                                    </tr>
                                                </tbody>
                                            </Table>
                                        </Card.Body>
                                    </Card>
                                    </SlideDown>
                                </th>
                            </tr>
                        </>
                    )) }
                </tbody>
            </Table>
        );
    }
}

export default Ranking;
