import axios from 'axios';


class Team {

    constructor() {
        this.teams = null;
    }

    async update() {
        let query = await axios({
            url: "http://140.112.174.222:1484/wind/fetch", 
            method: "post", 
            data: {
                mes: "123", 
            }, 
        });
        let data = query.data.result;
        data.sort((A, B) => (A.score < B.score) ? 1 : ((A.score > B.score) ? -1 : 0));
        console.log(data);

        let teams = new Array(data.length).fill({});

        let curRank = 1;
        for (let i = 0; i < data.length; i++) {
            if (i > 0 && data[i].score != data[i - 1].score){
                curRank++;
            }
            teams[i] = {
                "rank": curRank, 
                "ID": data[i].ID, 
                "name": data[i].name, 
                "score": data[i].score, 
                "history": data[i].history, 
            };
        }

        this.teams = teams;

        return teams;
    }
}

export default Team;
